mapper.update:
UPDATE smbms_user
userCode = #{userCode}, userName = #{userName}, userPassword = #{userPassword}, gender = #{gender}, birthday = #{birthday}, phone = #{phone}, address = #{address}, userRole = #{userRole}, createdBy = #{createdBy}, creationDate = #{creationDate}, modifyBy = #{modifyBy}, modifyDate = #{modifyDate},
WHERE id = #{id};

mapper.select:
SELECT *
FROM smbms_user WHERE userCode = #{userCode} and userPassword =
#{userPassword}

SELECT u.*,r.roleName FROM smbms_user u INNER JOIN smbms_role r ON
u.userRole = r.id

	
		and userName like CONCAT('%',#{userName},'%')
	
	
		and userRole = #{userRole}
	

limit #{page},#{pageSize}

SELECT count(1) FROM smbms_user u INNER JOIN smbms_role r ON u.userRole = r.id
and userName like CONCAT('%',#{userName},'%')
and userRole = #{userRole}
